import './Kanban.css';
import React, { useState } from "react";
import KanbanColumn from "./temp/KanbanColumn";




const App = () => {
  const [tasks, setTasks] = useState([
    { id: "1", title: "candidate 1", status: "Resume Shortlisted" },
    { id: "2", title: "candidate 2", status: "Assessment passed" },
    { id: "3", title: "candidate 3", status: "1st interview passed" },
    { id: "4", title: "candidate 4", status: "final interview passed" },
    { id: "5", title: "candidate 5", status: "job offered" }
  ]);

  const onDragStart = (e, taskId) => {
    e.dataTransfer.setData("taskId", taskId);
  };

  const onDragOver = (e) => {
    e.preventDefault(); // Necessary to allow dropping
  };

  const onDrop = (e, newStatus) => {
    const taskId = e.dataTransfer.getData("taskId");

    setTasks((prevTasks) =>
      prevTasks.map((task) =>
        task.id === taskId ? { ...task, status: newStatus } : task
      )
    );
  };

  return (
    <div className="kanban-board">
      <KanbanColumn
        title="To Do"
        tasks={tasks.filter((task) => task.status === "Resume Shortlisted")}
        onDrop={onDrop}
        onDragOver={onDragOver}
        onDragStart={onDragStart}
      />
      <KanbanColumn
        title="In Progress"
        tasks={tasks.filter((task) => task.status === "Assessment passed")}
        onDrop={onDrop}
        onDragOver={onDragOver}
        onDragStart={onDragStart}
      />
      <KanbanColumn
        title="Done"
        tasks={tasks.filter((task) => task.status === "1st interview passed")}
        onDrop={onDrop}
        onDragOver={onDragOver}
        onDragStart={onDragStart}
      />
      <KanbanColumn
        title="Done"
        tasks={tasks.filter((task) => task.status === "final interview passed")}
        onDrop={onDrop}
        onDragOver={onDragOver}
        onDragStart={onDragStart}
      />
      <KanbanColumn
        title="Done"
        tasks={tasks.filter((task) => task.status === "job offered")}
        onDrop={onDrop}
        onDragOver={onDragOver}
        onDragStart={onDragStart}
      />
    </div>
  );
};

export default App;
