//import '../Kanban.css' ;
import { Card, Avatar, Tag } from "antd"

const KanbanCard = ({ candidate, onDragStart ,stageColor }) => {
    return (
      <Card 
      style={{ 
        width: "95%", // Adjusted for better fit
        maxWidth: "230px", // Prevents it from expanding too much
        minHeight: "150px", 
        backgroundColor: "#fff",
        borderLeft: `5px solid ${stageColor}`,
        boxShadow: `0 2px 5px rgba(0,0,0,0.1)`,  
      }}
        className="candidate-card"
        draggable="true"
        onDragStart={(e) => onDragStart(e, candidate.id)}
      ><Card.Meta
      avatar={<Avatar style={{ marginRight: "20px" }} // Moves avatar to the left
      >{candidate.first_name[0]}{candidate.last_name[0]}</Avatar>}
      title={<div 
        style={{
          fontSize: "12px", // Slightly smaller to fit
          whiteSpace: "nowrap", // Prevents breaking into multiple lines
          overflow: "hidden", // Ensures text doesn't go out of bounds
          textOverflow: "ellipsis", // Adds "..." if the text is still too long
          width: "100%", // Uses the full width available
          display: "block", // Ensures it's treated as a block element
          textAlign: "center" // Centers the text
        }}
      >
        {candidate.first_name + " " + candidate.last_name}
      </div>}
      //description={<Tag color="blue">{candidate.status}</Tag>}
    />
      </Card>
    );
  };

  export default KanbanCard;
