const KanbanColumn = ({ stageCode , title, candidates, onDrop, onDragOver ,onDragStart, style }) => {
  return (
    <Card
      className="kanban-column"
      style={{ width: "250px", minHeight: "300px", backgroundColor: "#f4f4f4", borderRadius: "8px", padding: "10px" }}
      onDragOver={onDragOver}
      onDrop={(e) => onDrop(e, stageCode)}
    >
      {/* Styled Title Section */}
      <div style={{ 
        backgroundColor: style.backgroundColor, 
        color: "#fff", 
        padding: "8px", 
        textAlign: "center", 
        fontWeight: "bold",
        borderRadius: "6px" 
      }}>
        {title}
      </div>

      {/* Candidate Cards */}
      <div style={{ marginTop: "10px" }}>
        {candidates.map((candidate) => (
          <KanbanCard key={candidate.id} candidate={candidate} onDragStart={onDragStart} />
        ))}
      </div>
    </Card>
  );
};

export default KanbanColumn;
