//import '../Kanban.css' ;
import { Card, Avatar, Tag } from "antd"

const KanbanCard = ({ candidate, onDragStart ,stageColor }) => {
    return (
      <Card 
      style={{ 
        width: "100%", 
        maxWidth: "250px", 
        minHeight: "150px", 
        backgroundColor: "#fff",
        borderLeft: `5px solid ${stageColor}`,
        boxShadow: `0 2px 5px rgba(0,0,0,0.1)`,  
      }}
        className="candidate-card"
        draggable="true"
        onDragStart={(e) => onDragStart(e, candidate.id)}
      ><Card.Meta
      avatar={<Avatar style={{ marginLeft: "-25px" , marginTop: "-15px" }} // Moves avatar to the left
      >{candidate.first_name[0]}{candidate.last_name[0]}</Avatar>}
      title={
        <div>
          {/* Candidate Name */}
          <div
            style={{
              fontSize: "12px",
              whiteSpace: "nowrap",
              overflow: "hidden",
              textOverflow: "ellipsis",
              width: "100%",
              display: "block",
              textAlign: "left",
              marginTop: "-5px",
              fontWeight: "bold", 
            }}
          >
            {candidate.first_name + " " + candidate.last_name}
          </div>
          <div
            style={{
              fontSize: "10px",
              color: "#555",
              fontStyle: "italic",
              textAlign: "left",
              marginTop: "3px",
            }}
          >
            {candidate.current_job_title || "No Job Title"}
          </div>
        </div>}
      //description={<Tag color="blue">{candidate.status}</Tag>}
    />
      </Card>
    );
  };

  export default KanbanCard;
