import { List } from "antd";
import {Select} from "antd";
import KanbanBoard from "./kanbanBoard";
import { useEffect, useState } from "react";

const SelectJob = () => {

    useEffect(()=>{
        fetch("/hiring_pipeline.json") // Assuming it's in public/   
      .then((response) => response.json())
      .then((data) => {
        // Filter only top-level stages (ignoring sub-stages)
        const filteredStages = data.filter(stage => !stage.parent_progress_id);
      })
    },[]) ;

    const [selectedJob , setSelectedJob] = useState(null);

    const handleChange = (job) => {
        return setSelectedJob(job);
    }

return (
    <>
    <Select 
    style={{
        width: 120,
      }}
    onChange={(value)=>{handleChange(value)}}
    options = {
        [
          {
            value : 'job 1'
          } ,
          {
            value : 'job 2'
          }
        ]
    }
    >   
    </Select>
    {selectedJob && <KanbanBoard job = {selectedJob}></KanbanBoard>}
    </>
)
}

export default SelectJob ;
